import React, { Component } from 'react'
import axios from 'axios';

export default class Inicio extends Component {
    state={
        notas:[]
    }

        async componentDidMount(){
            const res = await axios.get('http://localhost:3001/api/Notas');
            this.setState({notas: res.data});

        }
    render() {

        return (

   <div>
        <section class="cards col-9">
                    {this.state.notas.map(nota => (
                        <article key={nota._id} style={{borderRadius:"1rem"}} class="card card--1">


                            <div class="card__info">
                                    <span class="card__category"> {nota}</span>
                                <h3 class="card__title" >{nota}</h3>
                                <h3 class="card__by">{nota}</h3>
                            </div>
                        </article>
                     ))}

                    </section>

   </div>

        )
    }
}
