import React, { Component } from 'react'
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { MostarAll } from '../../../backend/Routes/NotasRoutes';
export default class Inicio extends Component {
    state={
        notas:[]
    }

        async componentDidMount(){
            const res = await axios.get('http://localhost:3001/api/Notas',{headers:{'token':localStorage.getItem('token')}});
            this.setState({notas: res.data});

        }
    render() {

        return (

   <div >



            <form  onSubmit={this.onSubmitCrearNotas}>
             <Card className='col-md-4' variant="outlined">
      <CardContent>
        <Typography  color="textSecondary" gutterBottom>
        <label style={{color:"rgb(0, 0, 0)"}}  className="label"><h3>Titulo</h3></label>
        </Typography>
        <Typography variant="h5" component="h2">

        <input  style={{borderColor:"black"}} id="Titulo" name="Titulo" onChange={this.onChangeTitulo} type="text" className="input"/>
        </Typography>
        <Typography  color="textSecondary">
        <label style={{color:"rgb(0, 0, 0)"}} className="label"><h3>Nota</h3></label>

        </Typography>
        <Typography variant="body2" component="p">
        <input style={{borderColor:"black"}}  id="Nota" name="Nota" onChange={this.onChangeNota} type="text" className="input"/>

          <br />

        </Typography>
      </CardContent>
      <CardActions>
        <Button type='submit' size="small">Learn More</Button>
      </CardActions>
    </Card> 
    </form>

    {this.state.notas.map(nota => (
    <Card key={nota._id} className='col-md-4' variant="outlined">
      <CardContent>
        <Typography  color="textSecondary" gutterBottom>
        <label style={{color:"rgb(0, 0, 0)"}}  className="label"><h3>{nota.titulo}</h3></label>
        </Typography>
        <Typography  color="textSecondary">
        <label style={{color:"rgb(0, 0, 0)"}} className="label"><h3>{nota.nota}</h3></label>

        </Typography>
      </CardContent>
      <CardActions>
        <Button type='submit' size="small">Learn More</Button>
      </CardActions>
    </Card>
    ))}
   </div>

        )
    }
}
